import React, { useReducer } from 'react';
import axios from 'axios';

import ZonasContext from './zonasContext';
import zonasReducer from './zonasReducer';

import { GET_ZONAS, ZONAS_ERROR, LOADING_ZONAS } from '../types';

const ZonasState = (props) => {
  const initialState = {
    zonas: null,
    loading: false,
    current: null,
    filtered: null,
    error: null,
  };

  const [state, dispatch] = useReducer(zonasReducer, initialState);

  // Loading
  const loadingZonas = () => {
    const loading = true;
    dispatch({ type: LOADING_ZONAS, payload: loading });
  };

  // Get Zonas
  const getZonas = async () => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      loadingZonas();
      const res = await axios.get('api/v1/sectores', config);

      dispatch({ type: GET_ZONAS, payload: res.data });
    } catch (err) {
      dispatch({ type: ZONAS_ERROR, payload: err.response.msg });
    }
  };

  // // Filter Contacts
  // const filterContacts = (text) => {
  //   dispatch({ type: FILTER_CONTACTS, payload: text });
  // };

  // // Clear Filter
  // const clearFilter = () => {
  //   dispatch({ type: CLEAR_FILTER });
  // };

  // // Clear Contacts

  // const clearContacts = () => dispatch({ type: CLEAR_CONTACTS });

  return (
    <ZonasContext.Provider
      value={{
        zonas: state.zonas,
        loading: state.loading,
        current: state.current,
        filtered: state.filtered,
        error: state.error,
        // filterContacts,
        // clearFilter,
        getZonas,
        // clearContacts,
        loadingZonas,
      }}
    >
      {props.children}
    </ZonasContext.Provider>
  );
};

export default ZonasState;
